'use strict';
var winston = require('winston');
var path = require('path');

function Logger() {

}

function formatter(options) {
    var m = new Date();
    var dateString =
        m.getUTCFullYear() + '-' +
        ('0' + (m.getUTCMonth() + 1)).slice(-2) + '-' +
        ('0' + m.getUTCDate()).slice(-2) + ' ' +
        ('0' + m.getUTCHours()).slice(-2) + ':' +
        ('0' + m.getUTCMinutes()).slice(-2) + ':' +
        ('0' + m.getUTCSeconds()).slice(-2);
    return dateString + ' ' + options.level.toUpperCase() + ' ' + (undefined !== options.message ? options.message : '') + (options.meta && Object.keys(options.meta).length ? '\n\t' + JSON.stringify(options.meta) : '' );
}

var config = {
    exitOnError: false,
    level: process.env.NODE_DEBUG_LEVEL || 'debug',
    levels: {
        warn: 0,
        error: 1,
        debug: 2,

    },
    colors: {
        debug: 'cyan',
        warn: 'yellow',
        error: 'red'
    },
    // Production transports
    transports: [
        // Error log
        new winston.transports.File({
            name: 'error.log',
            filename: path.join(process.cwd(), 'logs/error.log'),
            level: 'error',
            json: false,
            formatter: formatter,
            zippedArchive: true,
            maxsize: 2 * 1024 * 1024 // 2mb
        })
    ],
    exceptionHandlers: [
        new winston.transports.File({
            name: 'exceptions.log',
            filename: path.join(process.cwd(), 'logs/exceptions.log'),
            maxsize: 2 * 1024 * 1024 // 2mb
        })
    ]
};

//debug file
if (config.level == 'debug') {
    config.transports.push(
        // Debug log
        new winston.transports.File({
            name: 'debug.log',
            level: 'debug',
            json: false,
            formatter: formatter,
            filename: path.join(process.cwd(), 'logs/debug.log'),
            zippedArchive: true,
            maxsize: 20 * 1024 * 1024 // 20mb
        })
    );
}

//console
if (process.env.NODE_ENV != 'production') {
    config.transports.push(
        //Console
        new (winston.transports.Console)({
            colorize: 'all'
        })
    );
}

/*************************************************************************
 SINGLETON CLASS DEFINITION
 *************************************************************************/
Logger.instance = null;

/**
 * Singleton getInstance definition
 * @return Logger class
 */
Logger.getInstance = function () {
    if (this.instance === null) {
        this.instance = new winston.Logger(config);
    }
    return this.instance;
}

module.exports = Logger.getInstance();